import React, { useEffect, useState } from 'react';

const StateWiseData = () =>{

    const [Data, setData] = useState([]);

    const getCovidCaseData = async() => {
        let response = await fetch('https://api.covid19india.org/data.json');
        response = await response.json();
        setData(response.statewise);
    }

    useEffect(()=>{
        getCovidCaseData();
    }, []);

    return (
        <>
            <h1 className=" text-center mt-5">INDIA COVID-19 Dashboard</h1>
            <br />
            <table id="example" className="table table-striped table-bordered text-center" style={{width:'70%'}}>
                <thead>
                    <tr>
                        <th>State</th>
                        <th>Confirmed</th>
                        <th>Recovered</th>
                        <th>Deaths</th>
                        <th>Active</th>
                        <th>lastupdatedtime</th>
                    </tr>
                </thead>
                <tbody>
                {
                    Data.map((currentElement)=>{
                        return (
                            <tr>
                                <td>{currentElement.state}</td>
                                <td>{currentElement.confirmed}</td>
                                <td>{currentElement.recovered}</td>
                                <td>{currentElement.deaths}</td>
                                <td>{currentElement.confirmed}</td>
                                <td>{currentElement.confirmed}</td>
                            </tr>
                        );
                    })
                }
                </tbody>
            </table>
        </>
    );
}

export default StateWiseData;
